package com.hi5adii.demos;public class AirConditioner extends Device{    int coolingCapacity;    public AirConditioner(String name, boolean on, String color, String brand, String model, int coolingCapacity) {        super(name, on, color, brand, model);        this.coolingCapacity = coolingCapacity;    }    public int getCoolingCapacity() {        return coolingCapacity;    }    public void setCoolingCapacity(int coolingCapacity) {        this.coolingCapacity = coolingCapacity;    }}