package com.hi5adii.demos;public class Fan extends Device{    int noOfBlades;    public Fan(String name, boolean on, String color, String brand, String model, int noOfBlades) {        super(name, on, color, brand, model);        this.noOfBlades = noOfBlades;    }    public int getNoOfBlades() {        return noOfBlades;    }    public void setNoOfBlades(int noOfBlades) {        this.noOfBlades = noOfBlades;    }}