package com.hi5adii.demos;public class Light extends Device{    int watt;    String intensity;    public Light(String name, boolean on, String color, String brand, String model, int watt, String intensity) {        super(name, on, color, brand, model);        this.watt = watt;        this.intensity = intensity;    }    public int getWatt() {        return watt;    }    public void setWatt(int watt) {        this.watt = watt;    }    public String getIntensity() {        return intensity;    }    public void setIntensity(String intensity) {        this.intensity = intensity;    }}