package com.hi5adii.demos;import java.util.Scanner;public class Main{    public static void main(String[] args)    {        HomeAutomation hm = new HomeAutomation();        Scanner sc = new Scanner(System.in);        boolean r = true;        while (r)        {            System.out.println("\u001B[36m+--------------------------------------+");            System.out.println("|           \u001B[34mHOME AUTOMATION\u001B[36m            |");            System.out.println("+-------+\u001B[36m------------------------------+");            System.out.println("| \u001B[33mSr No.\u001B[36m|          \u001B[33mOptions\u001B[36m             |");            System.out.println("+-------+\u001B[36m------------------------------+");            System.out.println("|   \u001B[31m1\u001B[36m   | \u001B[31mAdd Room                     \u001B[36m|");            System.out.println("|   \u001B[31m2\u001B[36m   | \u001B[31mAdd Device to Room           \u001B[36m|");            System.out.println("|   \u001B[31m3\u001B[36m   | \u001B[31mTurn On Device               \u001B[36m|");            System.out.println("|   \u001B[31m4\u001B[36m   | \u001B[31mTurn Off Device              \u001B[36m|");            System.out.println("|   \u001B[31m5\u001B[36m   | \u001B[31mCheck Device Status          \u001B[36m|");            System.out.println("|   \u001B[31m6\u001B[36m   | \u001B[31mCheck Duration               \u001B[36m|");            System.out.println("|   \u001B[31m7\u001B[36m   | \u001B[31mDisplay                      \u001B[36m|");            System.out.println("|   \u001B[31m8\u001B[36m   | \u001B[31mExit                         \u001B[36m|");            System.out.println("+-------+\u001B[36m------------------------------+\u001B[0m");            int choice = sc.nextInt();            sc.nextLine();            switch (choice)            {                case 1:                    hm.addRoom(sc);                    break;                case 2:                    hm.addDeviceToRoom(sc);                    break;                case 3:                    hm.turnOnDevice(sc);                    break;                case 4:                    hm.turnOffDevice(sc);                    break;                case 5:                    hm.checkDeviceStatus(sc);                    break;                case 6:                    hm.getTotalOnDuration(sc);                    break;                case 7:                    hm.display();                    break;                case 8:                    r = false;                    System.out.println("Exiting...");                    break;                default:                    System.out.println("Invalid choice. Please try again.");            }        }        sc.close();    }}