package com.hi5adii.demos;import java.util.ArrayList;import java.util.List;public class Room{    String name;    public List<Device> devices;    public Room(String name) {        this.name = name;        this.devices = new ArrayList<>();    }    public String getName() {        return name;    }    public void addDevice(Device device) {        devices.add(device);    }    public List<Device> getDevices() {        return devices;    }}