package com.hi5adii.demos;public class Shower extends Device{    boolean hasTempControl;    public Shower(String name, boolean on, String color, String brand, String model, boolean hasTempControl) {        super(name, on, color, brand, model);        this.hasTempControl = hasTempControl;    }    public boolean isHasTempControl() {        return hasTempControl;    }    public void setHasTempControl(boolean hasTempControl) {        this.hasTempControl = hasTempControl;    }}